import React, { useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import { getItems } from '../web3Client'


const styles = {
  item: {
    flexGrow: '1'
  },
  itemContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    margin: '0 10%',
  },
  itemInner: {
    margin: '10px',
    backgroundColor: 'red',
    height: '300px',
    width: '250px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
  }
}
function Home() {
  const [items, setItems] = useState([])

  useEffect(() => {
    getItems()
      .then(data => {
        console.log(data)
        setItems(data)
      })
      .catch(err => { console.log(err) })
  }, [])
  return (
    <div style={styles.itemContainer}>
      {items.map(((i) => {
        <div style={styles.item}>
          <div style={styles.itemInner}>
            <div style={{ flexGrow: '1', backgroundColor: 'gold' }}>
              <Link to='/product'>
                <div style={{ height: '100%' }}>
                  {i.title}
                </div>
              </Link>
            </div>
            <div >
              <button style={{ width: '100%', padding: '15px' }}>Bid</button>
            </div>
          </div>
        </div>
      }))}
    </div>
  );
}

export default Home;
