import React, { useState, useEffect } from 'react'
import { Button, Row, Col, Input, Label } from 'reactstrap';
import { getItems } from '../web3Client'
import CustomModal from '../components/modal'


const styles = {
    item: {
        // flexGrow: '1'
    },
    itemContainer: {
        display: 'flex',
        flexWrap: 'wrap',
        margin: '0 10%',
    },
    itemInner: {
        margin: '10px',
        backgroundColor: 'red',
        height: '300px',
        width: '250px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
    }
}
function AddItem() {
    const [items, setItems] = useState([])
    const [modalIsOpen, setIsOpen] = useState(false)

    useEffect(() => {
        getItems()
            .then(data => {
                console.log(data)
                setItems(data)
            })
            .catch(err => { console.log(err) })
    }, [])
    return (
        <div style={styles.itemContainer}>
            <form>
                <div>
                    <Input></Input>
                </div>
                <select>
                    <option>USD</option>
                    <option>ETH</option>
                </select>
                <input />
            </form>
            <CustomModal modalIsOpen={modalIsOpen} setIsOpen={setIsOpen}></CustomModal>
        </div>
    );
}

export default AddItem;
