import Web3 from 'web3'
// import auctionContractBuild from 'contracts/auction.json'
import auctionContractBuild from './auction.json'
let currentAccount
let auctionContract

export const initWeb3 = async () => {

    const { ethereum } = window

    if (!ethereum) {
        console.log('Please install Metamask..')
        return

    } else {
        try {
            const accounts = await ethereum.request({ method: 'eth_requestAccounts' })
            console.log('Found account! Address: ', accounts[0])

            if (accounts.lenght !== 0) {
                console.log('Authorized account found...')
                currentAccount = accounts[0]
            } else {
                console.log('No authorized account found!')
            }
        }catch(error){
            console.log(error)
        }
    }

    /**
     * log when account is changed
     */
    ethereum.on('accountsChanges', (accounts)=>{
        currentAccount = accounts[0]
        console.log('Account changed to: ', accounts[0])
    })

    const web3 = new Web3(ethereum)
    const networkId = await web3.eth.net.getId()
    auctionContract = web3.eth.Contract(auctionContractBuild.abi, auctionContractBuild.networks[networkId].address)
}

const callContract = () => {
    auctionContract.methods.set(14)
}
